"use strict";var animabar=document.querySelectorAll("[data-bar]"),animacircle=document.querySelectorAll("[data-circle]");function criarbarra(t,e){new ProgressBar.Line(e,{strokeWidth:4,easing:"easeInOut",duration:1400,color:"#38bf92",trailColor:"#eee",trailWidth:4,svgStyle:{width:"100%",height:"100%"},text:{style:{color:"#999",position:"absolute",right:"0",top:"30px",padding:0,margin:0,transform:null},autoStyleContainer:!1},from:{color:"#FFEA82"},to:{color:"#ED6A5A"},step:function(t,e){e.setText(Math.round(100*e.value())+" %")}}).animate(t)}function criarcirculo(t,e){new ProgressBar.Circle(e,{color:"#38bf92",strokeWidth:10,trailWidth:10,easing:"easeInOut",duration:1400,text:{autoStyleContainer:!1},step:function(t,e){e.setText(Math.round(100*e.value())+" %")}}).animate(t)}animabar.forEach(function(t){criarbarra((t.dataset.bar/100).toFixed(2),"#"+t.getAttribute("id"))}),animacircle.forEach(function(t){criarcirculo((t.dataset.circle/100).toFixed(2),"#"+t.getAttribute("id"))});var btn=document.querySelectorAll('[data-hover="hover"]');btn.forEach(function(t){t.addEventListener("mouseenter",function(t){this.classList.add("--select")}),t.addEventListener("mouseleave",function(){this.classList.remove("--select")})});